import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [documentId, setDocumentId] = useState(null);
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [sending, setSending] = useState(false);
  const [documentInfo, setDocumentInfo] = useState(null);
  const [notification, setNotification] = useState(null);
  const [uploadingToDrive, setUploadingToDrive] = useState(false);
  const [driveLink, setDriveLink] = useState(null);
  const [showUploadSuccess, setShowUploadSuccess] = useState(false);
  const [showDownloadAnimation, setShowDownloadAnimation] = useState(false);
  const [showDriveAnimation, setShowDriveAnimation] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const showNotification = (message, type = 'success') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 4000);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
      setShowUploadSuccess(false);
      setDriveLink(null);
    } else {
      showNotification('‚ö†Ô∏è Please select a PDF file', 'error');
    }
  };

  const handleUpload = async () => {
    if (!file) return showNotification('üìÅ Select a file first', 'error');

    setUploading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post(`${API_BASE_URL}/upload`, formData);
      setDocumentId(res.data.documentId);
      setChatId(res.data.chatId);
      setDocumentInfo({
        filename: res.data.filename,
        pageCount: res.data.pageCount,
        processingTime: res.data.processingTime
      });
      setMessages([{
        role: 'assistant',
        content: `üìÑ Document "${res.data.filename}" uploaded successfully! I've analyzed ${res.data.pageCount} pages. Feel free to ask me anything about your financial data!`,
        timestamp: new Date()
      }]);
      setShowUploadSuccess(true);
      showNotification('üéâ Document Uploaded & Analyzed Successfully!', 'success');
    } catch (err) {
      showNotification('‚ùå Upload failed: ' + (err.response?.data?.error || err.message), 'error');
    } finally {
      setUploading(false);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || !chatId) return;

    const userMsg = inputMessage.trim();
    setInputMessage('');
    setSending(true);
    setMessages(prev => [...prev, { role: 'user', content: userMsg, timestamp: new Date() }]);

    try {
      const res = await axios.post(`${API_BASE_URL}/chat`, { chatId, message: userMsg });
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: res.data.response,
        timestamp: new Date(),
        responseTime: res.data.responseTime
      }]);
    } catch {
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: '‚ùå Error occurred. Please try again.',
        timestamp: new Date(),
        isError: true
      }]);
    } finally {
      setSending(false);
    }
  };

  const handleDownloadExcel = async () => {
    if (!documentId) return showNotification('üì§ Upload a document first', 'error');

    setShowDownloadAnimation(true);
    try {
      const res = await axios.get(`${API_BASE_URL}/download/${documentId}`, { responseType: 'blob' });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.download = `${documentInfo.filename.replace('.pdf', '')}_extracted.xlsx`;
      link.click();
      window.URL.revokeObjectURL(url);
      
      setTimeout(() => {
        setShowDownloadAnimation(false);
        showNotification('üìä Excel Downloaded Successfully!', 'success');
      }, 2500);
    } catch {
      setShowDownloadAnimation(false);
      showNotification('‚ùå Download failed', 'error');
    }
  };

  const handleUploadToDrive = async () => {
    if (!documentId) return;
    
    setShowDriveAnimation(true);
    setUploadingToDrive(true);
    
    try {
      const res = await axios.post(`${API_BASE_URL}/download/drive/${documentId}`);
      
      setTimeout(() => {
        if (res.data.success) {
          setDriveLink(res.data.driveLink);
          setShowDriveAnimation(false);
          showNotification('‚òÅÔ∏è Uploaded to Google Drive Successfully!', 'success');
        }
      }, 2500);
    } catch (err) {
      setShowDriveAnimation(false);
      showNotification('‚ùå Google Drive upload failed: ' + (err.response?.data?.error || err.message), 'error');
    } finally {
      setTimeout(() => {
        setUploadingToDrive(false);
      }, 2500);
    }
  };

  return (
    <div className="App">
      {/* Download Animation Overlay */}
      {showDownloadAnimation && (
        <div className="animation-overlay">
          <div className="animation-content">
            <div className="animation-spinner"></div>
            <div className="animation-icon excel-icon">üìä</div>
            <h3>Downloading Excel File...</h3>
            <div className="animation-progress-bar">
              <div className="animation-progress-fill excel-progress"></div>
            </div>
            <p className="animation-subtitle">Preparing your financial report</p>
          </div>
        </div>
      )}

      {/* Google Drive Upload Animation */}
      {showDriveAnimation && (
        <div className="animation-overlay drive-overlay">
          <div className="animation-content">
            <div className="animation-spinner drive-spinner"></div>
            <div className="animation-icon drive-icon">‚òÅÔ∏è</div>
            <h3>Uploading to Google Drive...</h3>
            <div className="animation-progress-bar">
              <div className="animation-progress-fill drive-progress"></div>
            </div>
            <p className="animation-subtitle">Syncing to cloud storage</p>
          </div>
        </div>
      )}

      {/* Notification Toast */}
      {notification && (
        <div className={`notification-toast ${notification.type}`}>
          <div className="notification-content">
            <span className="notification-message">{notification.message}</span>
          </div>
        </div>
      )}

      {/* Background Effects */}
      <div className="background-particles">
        {[...Array(8)].map((_, i) => (
          <div key={i} className="particle" style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
            animationDelay: `${i * 2}s`,
            animationDuration: `${15 + i * 2}s`
          }}></div>
        ))}
      </div>

      <div className="container-wrapper">
        <header className="app-header">
          <div className="header-icon">üíº</div>
          <h1>Financial Document AI Chatbot</h1>
          <p>Upload ‚Ä¢ Analyze ‚Ä¢ Chat ‚Ä¢ Download ‚Ä¢ Share to Drive</p>
        </header>

        <div className="main-grid">
          {/* LEFT: Upload Section */}
          <div className="grid-card upload-card">
            <h2>
              <span className="card-icon">üì§</span>
              Upload Document
            </h2>
            
            <div className="file-input-wrapper">
              <input 
                type="file" 
                accept=".pdf" 
                onChange={handleFileChange} 
                disabled={uploading} 
                id="file-input" 
              />
              <label htmlFor="file-input" className={`file-input-label ${file ? 'has-file' : ''}`}>
                <div className="file-icon-wrapper">
                  <span className="file-upload-icon">
                    {file ? '‚úÖ' : 'üìÅ'}
                  </span>
                </div>
                <div className="file-upload-text">
                  {file ? file.name : 'Select PDF Document'}
                </div>
                <div className="file-upload-hint">
                  {file ? '‚ú® Ready to analyze!' : 'üëÜ Click to browse files'}
                </div>
              </label>
            </div>

            {showUploadSuccess && (
              <div className="upload-success-banner">
                <div className="success-icon-wrapper">
                  <div className="success-checkmark">‚úì</div>
                </div>
                <div className="success-text">
                  <strong>üéâ Upload Successful!</strong>
                  <p>Document analyzed and ready for questions</p>
                </div>
              </div>
            )}

            <button 
              onClick={handleUpload} 
              disabled={!file || uploading} 
              className="btn btn-primary"
            >
              {uploading ? (
                <>
                  <span className="btn-spinner"></span>
                  <span>Uploading & Analyzing...</span>
                </>
              ) : (
                <>
                  <span className="btn-icon">üöÄ</span>
                  <span>Upload & Analyze Document</span>
                </>
              )}
            </button>

            {documentInfo && (
              <div className="document-info-card">
                <h3>
                  <span className="info-icon">üìÑ</span>
                  Document Information
                </h3>
                <div className="info-grid">
                  <div className="info-item">
                    <span className="info-icon-small">üìù</span>
                    <div className="info-details">
                      <span className="info-label">Filename</span>
                      <span className="info-value">{documentInfo.filename}</span>
                    </div>
                  </div>
                  <div className="info-item">
                    <span className="info-icon-small">üìÉ</span>
                    <div className="info-details">
                      <span className="info-label">Pages</span>
                      <span className="info-value">{documentInfo.pageCount}</span>
                    </div>
                  </div>
                  <div className="info-item">
                    <span className="info-icon-small">‚è±Ô∏è</span>
                    <div className="info-details">
                      <span className="info-label">Processing Time</span>
                      <span className="info-value">{(documentInfo.processingTime / 1000).toFixed(2)}s</span>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {documentId && (
              <div className="actions-section">
                <div className="actions-header">
                  <div className="actions-icon">üéØ</div>
                  <div className="actions-text">
                    <h4>Export Options</h4>
                    <p>Download or sync to cloud</p>
                  </div>
                </div>

                <button 
                  onClick={handleDownloadExcel} 
                  className="btn btn-download"
                  disabled={showDownloadAnimation}
                >
                  <span className="btn-icon">üìä</span>
                  <span>Download Excel File</span>
                  <span className="btn-arrow">‚Üì</span>
                </button>

                <button
                  onClick={handleUploadToDrive}
                  disabled={uploadingToDrive}
                  className="btn btn-drive"
                >
                  <span className="btn-icon">‚òÅÔ∏è</span>
                  <span>{uploadingToDrive ? 'Uploading to Drive...' : 'Upload to Google Drive'}</span>
                  {!uploadingToDrive && <span className="btn-arrow">‚Üó</span>}
                </button>

                {driveLink && (
                  <div className="drive-success-card">
                    <div className="drive-success-header">
                      <div className="drive-success-icon-wrapper">
                        <div className="drive-success-checkmark">‚úì</div>
                      </div>
                      <span className="drive-success-text">Uploaded to Google Drive!</span>
                    </div>
                    <a 
                      href={driveLink} 
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="btn btn-view-drive"
                    >
                      <span className="btn-icon">üîó</span>
                      <span>Open in Google Drive</span>
                      <span className="btn-arrow">‚Üí</span>
                    </a>
                  </div>
                )}
              </div>
            )}
          </div>

          {/* RIGHT: Chat Section */}
          <div className="grid-card chat-card">
            <h2>
              <span className="card-icon">üí¨</span>
              AI Assistant
            </h2>
            
            <div className="messages-container">
              {messages.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-icon">ü§ñ</div>
                  <h3>AI Assistant Ready</h3>
                  <p>Upload a financial document to start the conversation!</p>
                </div>
              ) : (
                messages.map((msg, i) => (
                  <div key={i} className={`message ${msg.role}`}>
                    <div className="message-avatar-wrapper">
                      <div className="message-avatar">
                        {msg.role === 'user' ? 'üë§' : 'ü§ñ'}
                      </div>
                    </div>
                    <div className="message-bubble">
                      <div className="message-header">
                        <span className="message-role">
                          {msg.role === 'user' ? 'You' : 'AI Assistant'}
                        </span>
                        {msg.responseTime && (
                          <span className="response-time">
                            ‚ö° {(msg.responseTime / 1000).toFixed(2)}s
                          </span>
                        )}
                      </div>
                      <div className="message-content">{msg.content}</div>
                      <div className="message-timestamp">
                        {msg.timestamp.toLocaleTimeString()}
                      </div>
                    </div>
                  </div>
                ))
              )}
              {sending && (
                <div className="message assistant">
                  <div className="message-avatar-wrapper">
                    <div className="message-avatar typing">ü§ñ</div>
                  </div>
                  <div className="message-bubble typing-bubble">
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>

            <form onSubmit={handleSendMessage} className="chat-input-form">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="üí≠ Ask anything about your document..."
                disabled={sending || !chatId}
                className="chat-input"
              />
              <button 
                type="submit" 
                disabled={sending || !inputMessage.trim() || !chatId} 
                className="btn btn-send"
              >
                {sending ? (
                  <span className="btn-spinner"></span>
                ) : (
                  <>
                    <span className="btn-icon">üöÄ</span>
                    <span>Send</span>
                  </>
                )}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
