import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [documentId, setDocumentId] = useState(null);
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [sending, setSending] = useState(false);
  const [documentInfo, setDocumentInfo] = useState(null);
  const [notification, setNotification] = useState(null);
  const [uploadingToDrive, setUploadingToDrive] = useState(false);
  const [driveLink, setDriveLink] = useState(null);
  const [showUploadSuccess, setShowUploadSuccess] = useState(false);
  const [showDownloadAnimation, setShowDownloadAnimation] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const showNotification = (message, type = 'success') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 4000);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
      setShowUploadSuccess(false);
    } else {
      showNotification('Please select a PDF file', 'error');
    }
  };

  const handleUpload = async () => {
    if (!file) return showNotification('Select a file', 'error');

    setUploading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post(`${API_BASE_URL}/upload`, formData);
      setDocumentId(res.data.documentId);
      setChatId(res.data.chatId);
      setDocumentInfo({
        filename: res.data.filename,
        pageCount: res.data.pageCount,
        processingTime: res.data.processingTime
      });
      setMessages([{
        role: 'assistant',
        content: `Document "${res.data.filename}" uploaded! ${res.data.pageCount} pages analyzed.`,
        timestamp: new Date()
      }]);
      setShowUploadSuccess(true);
      showNotification('Document Uploaded Successfully! üéâ', 'success');
    } catch (err) {
      showNotification('Upload failed', 'error');
    } finally {
      setUploading(false);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || !chatId) return;

    const userMsg = inputMessage.trim();
    setInputMessage('');
    setSending(true);
    setMessages(prev => [...prev, { role: 'user', content: userMsg, timestamp: new Date() }]);

    try {
      const res = await axios.post(`${API_BASE_URL}/chat`, { chatId, message: userMsg });
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: res.data.response,
        timestamp: new Date(),
        responseTime: res.data.responseTime
      }]);
    } catch {
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Error. Try again.',
        timestamp: new Date(),
        isError: true
      }]);
    } finally {
      setSending(false);
    }
  };

  const handleDownloadExcel = async () => {
    if (!documentId) return showNotification('Upload first', 'error');

    setShowDownloadAnimation(true);
    try {
      const res = await axios.get(`${API_BASE_URL}/download/${documentId}`, { responseType: 'blob' });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.download = `${documentInfo.filename.replace('.pdf', '')}_extracted.xlsx`;
      link.click();
      window.URL.revokeObjectURL(url);
      
      setTimeout(() => {
        setShowDownloadAnimation(false);
        showNotification('Excel Downloaded Successfully! üìä', 'success');
      }, 2000);
    } catch {
      setShowDownloadAnimation(false);
      showNotification('Download failed', 'error');
    }
  };

  const handleUploadToDrive = async () => {
    if (!documentId) return;
    setUploadingToDrive(true);
    try {
      const res = await axios.post(`${API_BASE_URL}/download/drive/${documentId}`);
      if (res.data.success) {
        setDriveLink(res.data.driveLink);
        showNotification('Uploaded to Google Drive! ‚òÅÔ∏è', 'success');
      }
    } catch (err) {
      showNotification('Failed', 'error');
    } finally {
      setUploadingToDrive(false);
    }
  };

  return (
    <div className="App">
      {/* Download Animation Overlay */}
      {showDownloadAnimation && (
        <div className="download-animation-overlay">
          <div className="download-animation-content">
            <div className="download-spinner"></div>
            <div className="download-icon">üìä</div>
            <h3>Downloading Excel File...</h3>
            <div className="download-progress-bar">
              <div className="download-progress-fill"></div>
            </div>
          </div>
        </div>
      )}

      {/* Notification Toast */}
      {notification && (
        <div className={`notification-toast ${notification.type}`}>
          <div className="notification-content">
            <span className="notification-icon">
              {notification.type === 'success' ? '‚úÖ' : '‚ö†Ô∏è'}
            </span>
            <span className="notification-message">{notification.message}</span>
          </div>
        </div>
      )}

      {/* Background Particles */}
      <div className="background-particles">
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
      </div>

      <header className="app-header">
        <div className="header-icon">üíº</div>
        <h1>Financial Document Chatbot</h1>
        <p>Upload your financial documents and chat with AI</p>
      </header>

      <div className="main-container">
        <div className="upload-section">
          <div className="upload-card">
            <h2>
              <span className="section-icon">üì§</span>
              Upload Document
            </h2>
            
            <div className="file-input-wrapper">
              <input 
                type="file" 
                accept=".pdf" 
                onChange={handleFileChange} 
                disabled={uploading} 
                id="file-input" 
              />
              <label htmlFor="file-input" className={`file-input-label ${file ? 'has-file' : ''}`}>
                <span className="file-upload-icon">
                  {file ? '‚úÖ' : 'üìÅ'}
                </span>
                <div className="file-upload-text">
                  {file ? file.name : 'Select PDF Document'}
                </div>
                <div className="file-upload-hint">
                  {file ? 'Ready to upload!' : 'Click to browse files'}
                </div>
              </label>
            </div>

            {showUploadSuccess && (
              <div className="upload-success-banner">
                <div className="success-checkmark">‚úì</div>
                <div className="success-text">
                  <strong>Upload Successful!</strong>
                  <p>Your document is ready for analysis</p>
                </div>
              </div>
            )}

            <button 
              onClick={handleUpload} 
              disabled={!file || uploading} 
              className="btn btn-primary"
            >
              {uploading ? (
                <>
                  <span className="spinner"></span>
                  Uploading...
                </>
              ) : (
                <>
                  <span className="btn-icon">üöÄ</span>
                  Upload & Analyze
                </>
              )}
            </button>

            {documentInfo && (
              <div className="document-info">
                <h3>
                  <span className="info-icon">üìÑ</span>
                  Document Info
                </h3>
                <div className="info-grid">
                  <div className="info-item">
                    <span className="info-label">üìù Name:</span>
                    <span className="info-value">{documentInfo.filename}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">üìÉ Pages:</span>
                    <span className="info-value">{documentInfo.pageCount}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">‚è±Ô∏è Time:</span>
                    <span className="info-value">{(documentInfo.processingTime / 1000).toFixed(2)}s</span>
                  </div>
                </div>
              </div>
            )}

            {documentId && (
              <div className="download-section">
                <div className="download-banner">
                  <div className="download-banner-icon">üìä</div>
                  <div className="download-banner-text">
                    <h4>Excel File Ready!</h4>
                    <p>Download or upload to Google Drive</p>
                  </div>
                </div>

                <button 
                  onClick={handleDownloadExcel} 
                  className="btn btn-download"
                  disabled={showDownloadAnimation}
                >
                  <span className="btn-icon">‚¨áÔ∏è</span>
                  Download Excel File
                  <span className="download-arrow">‚Üì</span>
                </button>

                <button
                  onClick={handleUploadToDrive}
                  disabled={uploadingToDrive}
                  className="btn btn-drive"
                >
                  {uploadingToDrive ? (
                    <>
                      <span className="spinner"></span>
                      Uploading to Drive...
                    </>
                  ) : (
                    <>
                      <span className="btn-icon">‚òÅÔ∏è</span>
                      Upload to Google Drive
                    </>
                  )}
                </button>

                {driveLink && (
                  <div className="drive-success">
                    <div className="drive-success-content">
                      <div className="drive-success-icon">‚úì</div>
                      <span className="drive-success-text">Uploaded to Google Drive!</span>
                    </div>
                    <a 
                      href={driveLink} 
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="btn btn-view-drive"
                    >
                      <span className="btn-icon">üîó</span>
                      Open in Google Drive
                      <span className="external-icon">‚Üó</span>
                    </a>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        <div className="chat-section">
          <div className="chat-card">
            <h2>
              <span className="section-icon">üí¨</span>
              Chat with AI
            </h2>
            <div className="messages-container">
              {messages.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-icon">ü§ñ</div>
                  <p>Upload a document to start chatting!</p>
                </div>
              ) : (
                messages.map((msg, i) => (
                  <div key={i} className={`message ${msg.role}`}>
                    <div className="message-header">
                      <span className="message-avatar">
                        {msg.role === 'user' ? 'üë§' : 'ü§ñ'}
                      </span>
                      <span className="message-role">
                        {msg.role === 'user' ? 'You' : 'AI Assistant'}
                      </span>
                    </div>
                    <div className="message-content">{msg.content}</div>
                    <div className="message-timestamp">
                      {msg.timestamp.toLocaleTimeString()}
                    </div>
                  </div>
                ))
              )}
              <div ref={messagesEndRef} />
            </div>

            <form onSubmit={handleSendMessage} className="chat-input-form">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="Ask about the document..."
                disabled={sending || !chatId}
                className="chat-input"
              />
              <button 
                type="submit" 
                disabled={sending || !inputMessage.trim()} 
                className="btn btn-send"
              >
                {sending ? (
                  <span className="spinner"></span>
                ) : (
                  <>
                    <span className="btn-icon">üì®</span>
                    Send
                  </>
                )}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
