import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [documentId, setDocumentId] = useState(null);
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [sending, setSending] = useState(false);
  const [documentInfo, setDocumentInfo] = useState(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
    } else {
      alert('Please select a PDF file');
    }
  };

  const handleUpload = async () => {
    if (!file) {
      alert('Please select a file first');
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(`${API_BASE_URL}/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setDocumentId(response.data.documentId);
      setChatId(response.data.chatId);
      setDocumentInfo({
        filename: response.data.filename,
        pageCount: response.data.pageCount,
        processingTime: response.data.processingTime
      });
      
      setMessages([{
        role: 'assistant',
        content: `Document "${response.data.filename}" uploaded successfully! I've analyzed ${response.data.pageCount} pages. Ask me anything about this document.`,
        timestamp: new Date()
      }]);

      alert('File uploaded successfully!');
    } catch (error) {
      console.error('Upload error:', error);
      alert('Error uploading file: ' + (error.response?.data?.error || error.message));
    } finally {
      setUploading(false);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    
    if (!inputMessage.trim()) return;
    
    if (!chatId) {
      alert('Please upload a document first');
      return;
    }

    const userMessage = inputMessage.trim();
    setInputMessage('');
    setSending(true);

    // Add user message to UI
    setMessages(prev => [...prev, {
      role: 'user',
      content: userMessage,
      timestamp: new Date()
    }]);

    try {
      const response = await axios.post(`${API_BASE_URL}/chat`, {
        chatId: chatId,
        message: userMessage
      });

      // Add AI response to UI
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: response.data.response,
        timestamp: new Date(),
        responseTime: response.data.responseTime
      }]);

    } catch (error) {
      console.error('Chat error:', error);
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date(),
        isError: true
      }]);
    } finally {
      setSending(false);
    }
  };

  const handleDownloadExcel = async () => {
    if (!documentId) {
      alert('Please upload a document first');
      return;
    }

    try {
      console.log('Downloading Excel for document:', documentId);
      
      // Correct endpoint URL
      const response = await axios.get(`${API_BASE_URL}/upload/download/${documentId}`, {
        responseType: 'blob'
      });

      // Create blob from response
      const blob = new Blob([response.data], { 
        type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' 
      });
      
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      
      const filename = `${documentInfo?.filename.replace('.pdf', '')}_extracted_${Date.now()}.xlsx`;
      link.setAttribute('download', filename);
      
      document.body.appendChild(link);
      link.click();
      link.remove();
      
      // Clean up
      setTimeout(() => window.URL.revokeObjectURL(url), 100);

      alert('Excel file downloaded successfully!');
    } catch (error) {
      console.error('Download error:', error);
      alert('Error downloading file: ' + (error.response?.data?.error || error.message));
    }
  };

  const handleReset = () => {
    setFile(null);
    setDocumentId(null);
    setChatId(null);
    setMessages([]);
    setInputMessage('');
    setDocumentInfo(null);
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>💼 Financial Document Chatbot</h1>
        <p>Upload your financial documents and chat with AI</p>
      </header>

      <div className="main-container">
        {/* Upload Section */}
        <div className="upload-section">
          <div className="upload-card">
            <h2>📄 Upload Document</h2>
            <div className="file-input-wrapper">
              <input
                type="file"
                accept=".pdf"
                onChange={handleFileChange}
                disabled={uploading}
                id="file-input"
              />
              <label htmlFor="file-input" className="file-input-label">
                {file ? file.name : 'Choose PDF File'}
              </label>
            </div>

            <button
              onClick={handleUpload}
              disabled={!file || uploading}
              className="btn btn-primary"
            >
              {uploading ? 'Uploading...' : 'Upload & Analyze'}
            </button>

            {documentInfo && (
              <div className="document-info">
                <h3>📊 Document Info</h3>
                <p><strong>Name:</strong> {documentInfo.filename}</p>
                <p><strong>Pages:</strong> {documentInfo.pageCount}</p>
                <p><strong>Processing Time:</strong> {(documentInfo.processingTime / 1000).toFixed(2)}s</p>
              </div>
            )}

            {documentId && (
              <div className="action-buttons">
                <button onClick={handleDownloadExcel} className="btn btn-success">
                  📥 Download Excel
                </button>
                <button onClick={handleReset} className="btn btn-secondary">
                  🔄 New Document
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Chat Section */}
        <div className="chat-section">
          <div className="chat-card">
            <h2>💬 Chat with AI</h2>
            
            <div className="messages-container">
              {messages.length === 0 ? (
                <div className="empty-state">
                  <p>Upload a document to start chatting</p>
                </div>
              ) : (
                messages.map((msg, index) => (
                  <div key={index} className={`message ${msg.role}`}>
                    <div className="message-header">
                      <span className="message-role">
                        {msg.role === 'user' ? '👤 You' : '🤖 AI Assistant'}
                      </span>
                      {msg.responseTime && (
                        <span className="response-time">
                          ⏱️ {(msg.responseTime / 1000).toFixed(2)}s
                        </span>
                      )}
                    </div>
                    <div className="message-content">{msg.content}</div>
                    <div className="message-timestamp">
                      {msg.timestamp.toLocaleTimeString()}
                    </div>
                  </div>
                ))
              )}
              <div ref={messagesEndRef} />
            </div>

            <form onSubmit={handleSendMessage} className="chat-input-form">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="Ask about the document..."
                disabled={sending || !chatId}
                className="chat-input"
              />
              <button
                type="submit"
                disabled={sending || !chatId || !inputMessage.trim()}
                className="btn btn-send"
              >
                {sending ? '⏳' : '📤'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
