import React, { useState, useEffect, useRef } from 'react';

const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [documentId, setDocumentId] = useState(null);
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [sending, setSending] = useState(false);
  const [documentInfo, setDocumentInfo] = useState(null);
  const [notification, setNotification] = useState(null);
  const [uploadingToDrive, setUploadingToDrive] = useState(false);
  const [driveLink, setDriveLink] = useState(null);
  const [showUploadSuccess, setShowUploadSuccess] = useState(false);
  const [showDownloadAnimation, setShowDownloadAnimation] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const showNotification = (message, type = 'success') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 4000);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
      setShowUploadSuccess(false);
    } else {
      showNotification('Please select a PDF file', 'error');
    }
  };

  const handleUpload = async () => {
    if (!file) return showNotification('Select a file', 'error');

    setUploading(true);
    
    // Simulated upload
    setTimeout(() => {
      const mockResponse = {
        documentId: 'doc_' + Date.now(),
        chatId: 'chat_' + Date.now(),
        filename: file.name,
        pageCount: 18,
        processingTime: 643
      };
      
      setDocumentId(mockResponse.documentId);
      setChatId(mockResponse.chatId);
      setDocumentInfo({
        filename: mockResponse.filename,
        pageCount: mockResponse.pageCount,
        processingTime: mockResponse.processingTime
      });
      setMessages([{
        role: 'assistant',
        content: `Document "${mockResponse.filename}" uploaded! ${mockResponse.pageCount} pages analyzed.`,
        timestamp: new Date()
      }]);
      setShowUploadSuccess(true);
      showNotification('Document Uploaded Successfully! üéâ', 'success');
      setUploading(false);
    }, 2000);
  };

  const handleSendMessage = () => {
    if (!inputMessage.trim() || !chatId) return;

    const userMsg = inputMessage.trim();
    setInputMessage('');
    setSending(true);
    setMessages(prev => [...prev, { role: 'user', content: userMsg, timestamp: new Date() }]);

    setTimeout(() => {
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Hello! How can I help you with the financial document today?',
        timestamp: new Date(),
        responseTime: 1.2
      }]);
      setSending(false);
    }, 1000);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleDownloadExcel = () => {
    if (!documentId) return showNotification('Upload first', 'error');
    setShowDownloadAnimation(true);
    
    setTimeout(() => {
      setShowDownloadAnimation(false);
      showNotification('Excel Downloaded Successfully! üìä', 'success');
    }, 2000);
  };

  const handleUploadToDrive = () => {
    if (!documentId) return;
    setUploadingToDrive(true);
    
    setTimeout(() => {
      setDriveLink('https://drive.google.com/example');
      showNotification('Uploaded to Google Drive! ‚òÅÔ∏è', 'success');
      setUploadingToDrive(false);
    }, 2000);
  };

  return (
    <div style={styles.app}>
      {showDownloadAnimation && (
        <div style={styles.overlay}>
          <div style={styles.downloadContent}>
            <div style={styles.downloadSpinner}></div>
            <div style={styles.downloadIcon}>üìä</div>
            <h3 style={styles.downloadTitle}>Downloading Excel File...</h3>
            <div style={styles.progressBar}>
              <div style={styles.progressFill}></div>
            </div>
          </div>
        </div>
      )}

      {notification && (
        <div style={{...styles.notification, ...(notification.type === 'error' ? styles.notificationError : styles.notificationSuccess)}}>
          <span style={styles.notificationIcon}>{notification.type === 'success' ? '‚úÖ' : '‚ö†Ô∏è'}</span>
          <span>{notification.message}</span>
        </div>
      )}

      <div style={styles.particles}>
        {[...Array(5)].map((_, i) => (
          <div key={i} style={{...styles.particle, ...getParticleStyle(i)}}></div>
        ))}
      </div>

      <header style={styles.header}>
        <div style={styles.headerIcon}>üíº</div>
        <h1 style={styles.headerTitle}>Financial Document Chatbot</h1>
        <p style={styles.headerSubtitle}>Upload your financial documents and chat with AI</p>
      </header>

      <div style={styles.mainContainer}>
        <div style={styles.leftSection}>
          <div style={styles.card}>
            <h2 style={styles.cardTitle}>
              <span style={styles.sectionIcon}>üì§</span>
              Upload Document
            </h2>
            
            <div style={styles.fileInputWrapper}>
              <input 
                type="file" 
                accept=".pdf" 
                onChange={handleFileChange} 
                disabled={uploading} 
                id="file-input"
                style={styles.fileInput}
              />
              <label htmlFor="file-input" style={{...styles.fileLabel, ...(file ? styles.fileLabelHasFile : {})}}>
                <span style={styles.fileIcon}>{file ? '‚úÖ' : 'üìÅ'}</span>
                <div style={styles.fileText}>{file ? file.name : 'Select PDF Document'}</div>
                <div style={styles.fileHint}>{file ? 'Ready to upload!' : 'Click to browse files'}</div>
              </label>
            </div>

            {showUploadSuccess && (
              <div style={styles.successBanner}>
                <div style={styles.successCheck}>‚úì</div>
                <div>
                  <strong style={styles.successTitle}>Upload Successful!</strong>
                  <p style={styles.successText}>Your document is ready for analysis</p>
                </div>
              </div>
            )}

            <button 
              onClick={handleUpload} 
              disabled={!file || uploading} 
              style={{...styles.btn, ...styles.btnPrimary, ...(!file || uploading ? styles.btnDisabled : {})}}
            >
              {uploading ? (
                <>
                  <span style={styles.spinner}></span>
                  Uploading...
                </>
              ) : (
                <>
                  <span style={styles.btnIcon}>üöÄ</span>
                  Upload & Analyze
                </>
              )}
            </button>

            {documentInfo && (
              <div style={styles.documentInfo}>
                <h3 style={styles.infoTitle}>
                  <span>üìÑ</span>
                  Document Info
                </h3>
                <div style={styles.infoGrid}>
                  <div style={styles.infoItem}>
                    <span>üìù Name:</span>
                    <span style={styles.infoValue}>{documentInfo.filename}</span>
                  </div>
                  <div style={styles.infoItem}>
                    <span>üìÉ Pages:</span>
                    <span style={styles.infoValue}>{documentInfo.pageCount}</span>
                  </div>
                  <div style={styles.infoItem}>
                    <span>‚è±Ô∏è Time:</span>
                    <span style={styles.infoValue}>{(documentInfo.processingTime / 1000).toFixed(2)}s</span>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>

        <div style={styles.centerSection}>
          <div style={styles.card}>
            <h2 style={styles.cardTitle}>
              <span style={styles.sectionIcon}>üí¨</span>
              Chat with AI
            </h2>
            <div style={styles.messagesContainer}>
              {messages.length === 0 ? (
                <div style={styles.emptyState}>
                  <div style={styles.emptyIcon}>ü§ñ</div>
                  <p style={styles.emptyText}>Upload a document to start chatting!</p>
                </div>
              ) : (
                messages.map((msg, i) => (
                  <div key={i} style={{...styles.message, ...(msg.role === 'user' ? styles.messageUser : {})}}>
                    <div style={{...styles.messageHeader, ...(msg.role === 'user' ? styles.messageHeaderUser : {})}}>
                      <span style={styles.messageAvatar}>{msg.role === 'user' ? 'üë§' : 'ü§ñ'}</span>
                      <span style={styles.messageRole}>{msg.role === 'user' ? 'You' : 'AI Assistant'}</span>
                    </div>
                    <div style={{...styles.messageContent, ...(msg.role === 'user' ? styles.messageContentUser : styles.messageContentAssistant)}}>
                      {msg.content}
                    </div>
                    <div style={styles.messageTime}>{msg.timestamp.toLocaleTimeString()}</div>
                  </div>
                ))
              )}
              <div ref={messagesEndRef} />
            </div>

            <div style={styles.chatInputForm}>
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask about the document..."
                disabled={sending || !chatId}
                style={styles.chatInput}
              />
              <button 
                onClick={handleSendMessage}
                disabled={sending || !inputMessage.trim()} 
                style={{...styles.btn, ...styles.btnSend, ...(sending || !inputMessage.trim() ? styles.btnDisabled : {})}}
              >
                {sending ? <span style={styles.spinner}></span> : <><span style={styles.btnIcon}>üì®</span> Send</>}
              </button>
            </div>
          </div>
        </div>

        {documentId && (
          <div style={styles.rightSection}>
            <div style={styles.excelCard}>
              <div style={styles.excelHeader}>
                <div style={styles.excelIconLarge}>üìä</div>
                <h3 style={styles.excelTitle}>Excel File Ready!</h3>
                <p style={styles.excelSubtitle}>Download or upload to Google Drive</p>
              </div>

              <button 
                onClick={handleDownloadExcel} 
                style={{...styles.btn, ...styles.btnDownload}}
                disabled={showDownloadAnimation}
              >
                <span style={styles.btnIcon}>‚¨áÔ∏è</span>
                Download Excel File
                <span style={styles.downloadArrow}>‚Üì</span>
              </button>

              <div style={styles.divider}>
                <span style={styles.dividerText}>OR</span>
              </div>

              <button
                onClick={handleUploadToDrive}
                disabled={uploadingToDrive}
                style={{...styles.btn, ...styles.btnDrive, ...(uploadingToDrive ? styles.btnDisabled : {})}}
              >
                {uploadingToDrive ? (
                  <>
                    <span style={styles.spinner}></span>
                    Uploading to Drive...
                  </>
                ) : (
                  <>
                    <span style={styles.btnIcon}>‚òÅÔ∏è</span>
                    Upload to Google Drive
                  </>
                )}
              </button>

              {driveLink && (
                <div style={styles.driveSuccess}>
                  <div style={styles.driveSuccessContent}>
                    <div style={styles.driveSuccessIcon}>‚úì</div>
                    <span>Uploaded to Google Drive!</span>
                  </div>
                  <a 
                    href={driveLink} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    style={{...styles.btn, ...styles.btnViewDrive}}
                  >
                    <span style={styles.btnIcon}>üîó</span>
                    Open in Google Drive
                    <span>‚Üó</span>
                  </a>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

const getParticleStyle = (index) => {
  const positions = [
    { top: '10%', left: '10%' },
    { top: '70%', left: '80%' },
    { top: '40%', left: '50%' },
    { top: '80%', left: '20%' },
    { top: '20%', left: '70%' }
  ];
  return positions[index];
};

