import React, { useState, useEffect, useRef } from 'react';

const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [documentId, setDocumentId] = useState(null);
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [sending, setSending] = useState(false);
  const [documentInfo, setDocumentInfo] = useState(null);
  const [notification, setNotification] = useState(null);
  const [uploadingToDrive, setUploadingToDrive] = useState(false);
  const [driveLink, setDriveLink] = useState(null);
  const [showUploadSuccess, setShowUploadSuccess] = useState(false);
  const [showDownloadAnimation, setShowDownloadAnimation] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const showNotification = (message, type = 'success') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 4000);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
      setShowUploadSuccess(false);
    } else {
      showNotification('Please select a PDF file', 'error');
    }
  };

  const handleUpload = async () => {
    if (!file) return showNotification('Select a file', 'error');

    setUploading(true);
    
    setTimeout(() => {
      const mockResponse = {
        documentId: 'doc_' + Date.now(),
        chatId: 'chat_' + Date.now(),
        filename: file.name,
        pageCount: 18,
        processingTime: 643
      };
      
      setDocumentId(mockResponse.documentId);
      setChatId(mockResponse.chatId);
      setDocumentInfo({
        filename: mockResponse.filename,
        pageCount: mockResponse.pageCount,
        processingTime: mockResponse.processingTime
      });
      setMessages([{
        role: 'assistant',
        content: `Document "${mockResponse.filename}" uploaded! ${mockResponse.pageCount} pages analyzed.`,
        timestamp: new Date()
      }]);
      setShowUploadSuccess(true);
      showNotification('Document Uploaded Successfully! üéâ', 'success');
      setUploading(false);
    }, 2000);
  };

  const handleSendMessage = () => {
    if (!inputMessage.trim() || !chatId) return;

    const userMsg = inputMessage.trim();
    setInputMessage('');
    setSending(true);
    setMessages(prev => [...prev, { role: 'user', content: userMsg, timestamp: new Date() }]);

    setTimeout(() => {
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Hello! How can I help you with the financial document today?',
        timestamp: new Date(),
        responseTime: 1.2
      }]);
      setSending(false);
    }, 1000);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleDownloadExcel = () => {
    if (!documentId) return showNotification('Upload first', 'error');
    setShowDownloadAnimation(true);
    
    setTimeout(() => {
      setShowDownloadAnimation(false);
      showNotification('Excel Downloaded Successfully! üìä', 'success');
    }, 2000);
  };

  const handleUploadToDrive = () => {
    if (!documentId) return;
    setUploadingToDrive(true);
    
    setTimeout(() => {
      setDriveLink('https://drive.google.com/example');
      showNotification('Uploaded to Google Drive! ‚òÅÔ∏è', 'success');
      setUploadingToDrive(false);
    }, 2000);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-600 via-purple-600 to-pink-500 p-8 font-sans relative overflow-hidden">
      {/* Animated background particles */}
      <div className="fixed inset-0 pointer-events-none">
        {[...Array(8)].map((_, i) => (
          <div
            key={i}
            className="absolute w-64 h-64 bg-white opacity-5 rounded-full blur-3xl animate-float"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${i * 0.5}s`,
              animationDuration: `${15 + i * 2}s`
            }}
          />
        ))}
      </div>

      {/* Download Animation */}
      {showDownloadAnimation && (
        <div className="fixed inset-0 bg-black bg-opacity-90 backdrop-blur-md flex items-center justify-center z-50 animate-fadeIn">
          <div className="bg-white rounded-3xl p-12 text-center shadow-2xl max-w-md animate-scaleIn">
            <div className="w-24 h-24 border-8 border-indigo-100 border-t-indigo-600 rounded-full animate-spin mx-auto mb-6" />
            <div className="text-7xl mb-6 animate-bounce">üìä</div>
            <h3 className="text-3xl font-bold text-indigo-600 mb-6">Downloading Excel File...</h3>
            <div className="w-full h-3 bg-indigo-100 rounded-full overflow-hidden">
              <div className="h-full bg-gradient-to-r from-indigo-600 via-purple-600 to-indigo-600 rounded-full animate-progress" style={{backgroundSize: '200% 100%'}} />
            </div>
          </div>
        </div>
      )}

      {/* Notification */}
      {notification && (
        <div className={`fixed top-8 right-8 px-8 py-5 rounded-2xl shadow-2xl z-50 flex items-center gap-4 text-white font-semibold text-lg animate-slideInRight ${
          notification.type === 'success' 
            ? 'bg-gradient-to-r from-emerald-500 to-teal-500' 
            : 'bg-gradient-to-r from-rose-500 to-pink-500'
        }`}>
          <span className="text-3xl">{notification.type === 'success' ? '‚úÖ' : '‚ö†Ô∏è'}</span>
          <span>{notification.message}</span>
        </div>
      )}

      {/* Header */}
      <header className="text-center text-white mb-12 relative z-10">
        <div className="text-8xl mb-6 animate-float">üíº</div>
        <h1 className="text-6xl font-bold mb-4 drop-shadow-2xl">Financial Document Chatbot</h1>
        <p className="text-2xl opacity-95 drop-shadow-lg">Upload your financial documents and chat with AI</p>
      </header>

      {/* Main Container - 75% width */}
      <div className="max-w-[75%] mx-auto grid grid-cols-[400px_1fr_420px] gap-8 relative z-10">
        {/* Left Section - Upload */}
        <div className="bg-white bg-opacity-95 rounded-3xl p-10 shadow-2xl backdrop-blur-sm border border-white border-opacity-30">
          <h2 className="text-3xl font-bold mb-8 text-gray-800 border-b-4 border-indigo-600 pb-4 flex items-center gap-3">
            <span className="text-4xl">üì§</span>
            Upload Document
          </h2>
          
          {/* File Input */}
          <div className="mb-8">
            <input 
              type="file" 
              accept=".pdf" 
              onChange={handleFileChange} 
              disabled={uploading} 
              id="file-input"
              className="hidden"
            />
            <label 
              htmlFor="file-input" 
              className={`block p-10 border-4 border-dashed rounded-2xl cursor-pointer text-center font-semibold transition-all duration-300 hover:scale-105 hover:shadow-xl ${
                file 
                  ? 'bg-gradient-to-br from-amber-100 to-orange-200 border-orange-400 text-orange-900' 
                  : 'bg-gradient-to-br from-gray-50 to-gray-100 border-indigo-400 text-indigo-600 hover:from-indigo-50 hover:to-purple-50'
              }`}
            >
              <span className="text-6xl block mb-4">{file ? '‚úÖ' : 'üìÅ'}</span>
              <div className="text-xl font-bold mb-2">{file ? file.name : 'Select PDF Document'}</div>
              <div className="text-sm opacity-80">{file ? 'Ready to upload!' : 'Click to browse files'}</div>
            </label>
          </div>

          {/* Success Banner */}
          {showUploadSuccess && (
            <div className="bg-gradient-to-r from-emerald-100 to-teal-100 rounded-2xl p-6 mb-6 flex items-center gap-4 shadow-lg border-2 border-emerald-300 animate-slideInUp">
              <div className="w-14 h-14 bg-white rounded-full flex items-center justify-center text-3xl text-emerald-500 flex-shrink-0 shadow-md">‚úì</div>
              <div>
                <strong className="block text-emerald-900 text-lg mb-1">Upload Successful!</strong>
                <p className="text-emerald-700 text-sm">Your document is ready for analysis</p>
              </div>
            </div>
          )}

          {/* Upload Button */}
          <button 
            onClick={handleUpload} 
            disabled={!file || uploading} 
            className="w-full py-5 rounded-2xl text-xl font-bold text-white bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 hover:scale-105 shadow-xl flex items-center justify-center gap-3 mb-8"
          >
            {uploading ? (
              <>
                <div className="w-6 h-6 border-4 border-white border-t-transparent rounded-full animate-spin" />
                Uploading...
              </>
            ) : (
              <>
                <span className="text-3xl">üöÄ</span>
                Upload & Analyze
              </>
            )}
          </button>

          {/* Document Info */}
          {documentInfo && (
            <div className="bg-gradient-to-br from-violet-100 to-purple-100 rounded-2xl p-8 border-l-8 border-indigo-600 shadow-lg animate-slideInLeft">
              <h3 className="text-indigo-700 mb-6 text-2xl font-bold flex items-center gap-3">
                <span className="text-3xl">üìÑ</span>
                Document Info
              </h3>
              <div className="space-y-4">
                <div className="flex justify-between items-center p-4 bg-white rounded-xl shadow-sm">
                  <span className="font-semibold text-indigo-600 flex items-center gap-2">
                    <span>üìù</span> Name:
                  </span>
                  <span className="font-bold text-gray-800 text-sm truncate ml-2 max-w-[180px]">{documentInfo.filename}</span>
                </div>
                <div className="flex justify-between items-center p-4 bg-white rounded-xl shadow-sm">
                  <span className="font-semibold text-indigo-600 flex items-center gap-2">
                    <span>üìÉ</span> Pages:
                  </span>
                  <span className="font-bold text-gray-800">{documentInfo.pageCount}</span>
                </div>
                <div className="flex justify-between items-center p-4 bg-white rounded-xl shadow-sm">
                  <span className="font-semibold text-indigo-600 flex items-center gap-2">
                    <span>‚è±Ô∏è</span> Time:
                  </span>
                  <span className="font-bold text-gray-800">{(documentInfo.processingTime / 1000).toFixed(2)}s</span>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Center Section - Chat */}
        <div className="bg-white bg-opacity-95 rounded-3xl p-10 shadow-2xl backdrop-blur-sm border border-white border-opacity-30 flex flex-col">
          <h2 className="text-3xl font-bold mb-8 text-gray-800 border-b-4 border-indigo-600 pb-4 flex items-center gap-3">
            <span className="text-4xl">üí¨</span>
            Chat with AI
          </h2>
          
          {/* Messages Container */}
          <div className="flex-1 overflow-y-auto p-8 bg-gradient-to-br from-gray-50 to-gray-100 rounded-2xl mb-8 min-h-[500px] max-h-[600px]">
            {messages.length === 0 ? (
              <div className="text-center py-24 text-indigo-600">
                <div className="text-9xl mb-8 animate-float">ü§ñ</div>
                <p className="text-2xl font-semibold">Upload a document to start chatting!</p>
              </div>
            ) : (
              messages.map((msg, i) => (
                <div key={i} className={`mb-8 animate-messageSlide ${msg.role === 'user' ? 'text-right' : ''}`}>
                  <div className={`flex items-center gap-3 mb-3 ${msg.role === 'user' ? 'justify-end' : ''}`}>
                    <span className="text-4xl">{msg.role === 'user' ? 'üë§' : 'ü§ñ'}</span>
                    <span className="font-bold text-lg text-indigo-600">{msg.role === 'user' ? 'You' : 'AI Assistant'}</span>
                  </div>
                  <div className={`inline-block max-w-[85%] p-6 rounded-2xl shadow-lg text-lg leading-relaxed ${
                    msg.role === 'user' 
                      ? 'bg-gradient-to-br from-indigo-600 to-purple-600 text-white' 
                      : 'bg-white text-gray-800 border-2 border-indigo-100'
                  }`}>
                    {msg.content}
                  </div>
                  <div className="text-sm text-gray-500 mt-2">{msg.timestamp.toLocaleTimeString()}</div>
                </div>
              ))
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Chat Input */}
          <div className="flex gap-4">
            <input
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask about the document..."
              disabled={sending || !chatId}
              className="flex-1 p-5 border-4 border-indigo-200 rounded-2xl text-lg outline-none focus:border-indigo-500 focus:ring-4 focus:ring-indigo-200 transition-all"
            />
            <button 
              onClick={handleSendMessage}
              disabled={sending || !inputMessage.trim()} 
              className="px-10 py-5 rounded-2xl text-lg font-bold text-white bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 hover:scale-105 shadow-xl flex items-center gap-3"
            >
              {sending ? (
                <div className="w-6 h-6 border-4 border-white border-t-transparent rounded-full animate-spin" />
              ) : (
                <>
                  <span className="text-2xl">üì®</span>
                  Send
                </>
              )}
            </button>
          </div>
        </div>

        {/* Right Section - Excel */}
        {documentId && (
          <div className="bg-white bg-opacity-95 rounded-3xl p-10 shadow-2xl backdrop-blur-sm border border-white border-opacity-30 animate-slideInRight">
            <div className="text-center mb-10 p-10 bg-gradient-to-br from-emerald-100 to-teal-100 rounded-2xl">
              <div className="text-8xl mb-4 animate-bounce">üìä</div>
              <h3 className="text-emerald-800 text-3xl font-bold mb-2">Excel File Ready!</h3>
              <p className="text-emerald-700 text-lg">Download or upload to Google Drive</p>
            </div>

            <button 
              onClick={handleDownloadExcel} 
              className="w-full py-5 rounded-2xl text-lg font-bold text-white bg-gradient-to-r from-emerald-500 to-teal-500 hover:from-emerald-600 hover:to-teal-600 transition-all duration-300 hover:scale-105 shadow-xl flex items-center justify-center gap-3 mb-6"
              disabled={showDownloadAnimation}
            >
              <span className="text-2xl">‚¨áÔ∏è</span>
              Download Excel File
              <span className="text-2xl animate-bounce">‚Üì</span>
            </button>

            <div className="flex items-center my-6">
              <div className="flex-1 h-0.5 bg-gradient-to-r from-transparent via-gray-300 to-transparent" />
              <span className="px-4 text-gray-500 font-bold">OR</span>
              <div className="flex-1 h-0.5 bg-gradient-to-r from-transparent via-gray-300 to-transparent" />
            </div>

            <button
              onClick={handleUploadToDrive}
              disabled={uploadingToDrive}
              className="w-full py-5 rounded-2xl text-lg font-bold text-white bg-gradient-to-r from-blue-500 to-green-500 hover:from-blue-600 hover:to-green-600 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 hover:scale-105 shadow-xl flex items-center justify-center gap-3"
            >
              {uploadingToDrive ? (
                <>
                  <div className="w-6 h-6 border-4 border-white border-t-transparent rounded-full animate-spin" />
                  Uploading to Drive...
                </>
              ) : (
                <>
                  <span className="text-2xl">‚òÅÔ∏è</span>
                  Upload to Google Drive
                </>
              )}
            </button>

            {driveLink && (
              <div className="mt-8 bg-gradient-to-br from-emerald-100 to-teal-100 rounded-2xl p-8 border-l-8 border-blue-500 shadow-lg animate-slideInUp">
                <div className="flex items-center gap-4 mb-6">
                  <div className="w-14 h-14 bg-white rounded-full flex items-center justify-center text-3xl text-blue-500 shadow-md">‚úì</div>
                  <span className="font-bold text-emerald-900 text-lg">Uploaded to Google Drive!</span>
                </div>
                <a 
                  href={driveLink} 
                  target="_blank" 
                  rel="noopener noreferrer" 
                  className="w-full py-5 rounded-2xl text-lg font-bold text-white bg-gradient-to-r from-blue-500 to-green-500 hover:from-blue-600 hover:to-green-600 transition-all duration-300 hover:scale-105 shadow-xl flex items-center justify-center gap-3"
                >
                  <span className="text-2xl">üîó</span>
                  Open in Google Drive
                  <span className="text-xl">‚Üó</span>
                </a>
              </div>
            )}
          </div>
        )}
      </div>

      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-20px); }
        }
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        @keyframes scaleIn {
          from { opacity: 0; transform: scale(0.8); }
          to { opacity: 1; transform: scale(1); }
        }
        @keyframes slideInRight {
          from { opacity: 0; transform: translateX(100px); }
          to { opacity: 1; transform: translateX(0); }
        }
        @keyframes slideInUp {
          from { opacity: 0; transform: translateY(30px); }
          to { opacity: 1; transform: translateY(0); }
        }
        @keyframes slideInLeft {
          from { opacity: 0; transform: translateX(-30px); }
          to { opacity: 1; transform: translateX(0); }
        }
        @keyframes messageSlide {
          from { opacity: 0; transform: translateY(15px); }
          to { opacity: 1; transform: translateY(0); }
        }
        @keyframes progress {
          from { width: 0%; }
          to { width: 100%; }
        }
        .animate-float { animation: float 3s ease-in-out infinite; }
        .animate-fadeIn { animation: fadeIn 0.3s ease-out; }
        .animate-scaleIn { animation: scaleIn 0.4s ease-out; }
        .animate-slideInRight { animation: slideInRight 0.6s ease-out; }
        .animate-slideInUp { animation: slideInUp 0.5s ease-out; }
        .animate-slideInLeft { animation: slideInLeft 0.6s ease-out; }
        .animate-messageSlide { animation: messageSlide 0.4s ease-out; }
        .animate-progress { animation: progress 2s ease-in-out forwards; }
      `}</style>
    </div>
  );
}

export default App;
