import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [documentId, setDocumentId] = useState(null);
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [sending, setSending] = useState(false);
  const [documentInfo, setDocumentInfo] = useState(null);
  const [notification, setNotification] = useState(null);
  const [uploadingToDrive, setUploadingToDrive] = useState(false); // ‚Üê NEW
  const [driveLink, setDriveLink] = useState(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Show notification with auto-dismiss
  const showNotification = (message, type = 'success') => {
    setNotification({ message, type });
    setTimeout(() => {
      setNotification(null);
    }, 4000);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
    } else {
      showNotification('Please select a PDF file', 'error');
    }
  };

  const handleUpload = async () => {
    if (!file) {
      showNotification('Please select a file first', 'error');
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(`${API_BASE_URL}/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setDocumentId(response.data.documentId);
      setChatId(response.data.chatId);
      setDocumentInfo({
        filename: response.data.filename,
        pageCount: response.data.pageCount,
        processingTime: response.data.processingTime
      });
      
      setMessages([{
        role: 'assistant',
        content: `Document "${response.data.filename}" uploaded successfully! I've analyzed ${response.data.pageCount} pages. Ask me anything about this document.`,
        timestamp: new Date()
      }]);

      showNotification(`‚ú® ${response.data.filename} uploaded and analyzed successfully!`, 'success');
    } catch (error) {
      console.error('Upload error:', error);
      showNotification('Error uploading file: ' + (error.response?.data?.error || error.message), 'error');
    } finally {
      setUploading(false);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    
    if (!inputMessage.trim()) return;
    
    if (!chatId) {
      showNotification('Please upload a document first', 'error');
      return;
    }

    const userMessage = inputMessage.trim();
    setInputMessage('');
    setSending(true);

    setMessages(prev => [...prev, {
      role: 'user',
      content: userMessage,
      timestamp: new Date()
    }]);

    try {
      const response = await axios.post(`${API_BASE_URL}/chat`, {
        chatId: chatId,
        message: userMessage
      });

      setMessages(prev => [...prev, {
        role: 'assistant',
        content: response.data.response,
        timestamp: new Date(),
        responseTime: response.data.responseTime
      }]);

    } catch (error) {
      console.error('Chat error:', error);
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date(),
        isError: true
      }]);
    } finally {
      setSending(false);
    }
  };

  const handleDownloadExcel = async () => {
    if (!documentId) {
      showNotification('Please upload a document first', 'error');
      return;
    }

    try {
      console.log('Downloading Excel for document:', documentId);
      
      const response = await axios.get(`${API_BASE_URL}/download/${documentId}`, {
        responseType: 'blob',
        timeout: 30000
      });

      const blob = new Blob([response.data], { 
        type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' 
      });
      
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      
      let filename;
      const contentDisposition = response.headers['content-disposition'];
      if (contentDisposition) {
        filename = contentDisposition.split('filename=')[1].replace(/"/g, '');
      } else {
        filename = `${documentInfo?.filename.replace('.pdf', '')}_extracted_${Date.now()}.xlsx`;
      }
      
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      console.log('‚úÖ Excel file downloaded successfully!');
      showNotification(`üìä Excel file downloaded successfully!`, 'success');
      
    } catch (error) {
      console.error('Download error details:', {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data
      });
      
      if (error.response?.status === 404) {
        console.log('üîÑ Trying legacy download endpoint...');
        try {
          const legacyResponse = await axios.get(`${API_BASE_URL}/upload/download/${documentId}`, {
            responseType: 'blob'
          });

          const blob = new Blob([legacyResponse.data], { 
            type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' 
          });
          
          const url = window.URL.createObjectURL(blob);
          const link = document.createElement('a');
          link.href = url;
          link.setAttribute('download', `${documentInfo?.filename.replace('.pdf', '')}_extracted_${Date.now()}.xlsx`);
          document.body.appendChild(link);
          link.click();
          document.body.removeChild(link);
          window.URL.revokeObjectURL(url);
          
          console.log('‚úÖ Excel file downloaded via legacy endpoint!');
          showNotification(`üìä Excel file downloaded successfully!`, 'success');
          return;
        } catch (legacyError) {
          console.error('Legacy endpoint also failed:', legacyError);
        }
      }
      
      showNotification('Error downloading file: ' + (error.response?.data?.error || error.message), 'error');
    }
  };

  return (
    <div className="App">
      {/* Notification Toast */}
      {notification && (
        <div className={`notification-toast ${notification.type}`}>
          <div className="notification-content">
            <span className="notification-icon">
              {notification.type === 'success' ? '‚úÖ' : '‚ö†Ô∏è'}
            </span>
            <span className="notification-message">{notification.message}</span>
          </div>
        </div>
      )}

      <header className="app-header">
        <h1>üíº Financial Document Chatbot</h1>
        <p>Upload your financial documents and chat with AI</p>
      </header>

      <div className="main-container">
        {/* Upload Section */}
        <div className="upload-section">
          <div className="upload-card">
            <h2>üìÑ Upload Document</h2>
            <div className="file-input-wrapper">
              <input
                type="file"
                accept=".pdf"
                onChange={handleFileChange}
                disabled={uploading}
                id="file-input"
              />
              <label htmlFor="file-input" className={`file-input-label ${file ? 'has-file' : ''}`}>
                <span className="file-upload-icon">
                  {file ? '‚úÖ' : 'üìÅ'}
                </span>
                <div className="file-upload-text">
                  {file ? file.name : 'Select PDF Document'}
                </div>
                <div className="file-upload-hint">
                  {file ? 'File ready to upload' : 'Click here to browse files'}
                </div>
              </label>
            </div>

            <button
              onClick={handleUpload}
              disabled={!file || uploading}
              className="btn btn-primary"
            >
              {uploading ? (
                <>
                  <span className="spinner"></span>
                  Uploading...
                </>
              ) : (
                'üì§ Upload & Analyze'
              )}
            </button>

            {documentInfo && (
              <div className="document-info">
                <h3>üìä Document Info</h3>
                <div className="info-grid">
                  <div className="info-item">
                    <span className="info-label">Name:</span>
                    <span className="info-value">{documentInfo.filename}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Pages:</span>
                    <span className="info-value">{documentInfo.pageCount}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Processing Time:</span>
                    <span className="info-value">{(documentInfo.processingTime / 1000).toFixed(2)}s</span>
                  </div>
                </div>
              </div>
            )}

            {documentId && (
              <div className="download-section">
                <div className="download-banner">
                  <div className="download-icon">üìä</div>
                  <div className="download-text">
                    <h4>Excel Report Ready</h4>
                    <p>Your document has been processed and converted</p>
                  </div>
                </div>
                <button onClick={handleDownloadExcel} className="btn btn-download">
                  <span className="download-arrow">‚¨á</span>
                  Download Excel File
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Chat Section */}
        <div className="chat-section">
          <div className="chat-card">
            <h2>üí¨ Chat with AI</h2>
            
            <div className="messages-container">
              {messages.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-icon">üìÑ</div>
                  <p>Upload a document to start chatting</p>
                </div>
              ) : (
                messages.map((msg, index) => (
                  <div key={index} className={`message ${msg.role}`}>
                    <div className="message-header">
                      <span className="message-role">
                        {msg.role === 'user' ? 'üë§ You' : 'ü§ñ AI Assistant'}
                      </span>
                      {msg.responseTime && (
                        <span className="response-time">
                          ‚è±Ô∏è {(msg.responseTime / 1000).toFixed(2)}s
                        </span>
                      )}
                    </div>
                    <div className="message-content">{msg.content}</div>
                    <div className="message-timestamp">
                      {msg.timestamp.toLocaleTimeString()}
                    </div>
                  </div>
                ))
              )}
              <div ref={messagesEndRef} />
            </div>

            <form onSubmit={handleSendMessage} className="chat-input-form">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="Ask about the document..."
                disabled={sending || !chatId}
                className="chat-input"
              />
              <button
                type="submit"
                disabled={sending || !chatId || !inputMessage.trim()}
                className="btn btn-send"
              >
                {sending ? '‚è≥' : 'üì§'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
