import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [documentId, setDocumentId] = useState(null);
  const [chatId, setChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [sending, setSending] = useState(false);
  const [documentInfo, setDocumentInfo] = useState(null);
  const [notification, setNotification] = useState(null);
  const [uploadingToDrive, setUploadingToDrive] = useState(false);
  const [driveLink, setDriveLink] = useState(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const showNotification = (message, type = 'success') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 4000);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
    } else {
      showNotification('Please select a PDF file', 'error');
    }
  };

  const handleUpload = async () => {
    if (!file) {
      showNotification('Please select a file first', 'error');
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(`${API_BASE_URL}/upload`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setDocumentId(response.data.documentId);
      setChatId(response.data.chatId);
      setDocumentInfo({
        filename: response.data.filename,
        pageCount: response.data.pageCount,
        processingTime: response.data.processingTime
      });

      setMessages([{
        role: 'assistant',
        content: `Document "${response.data.filename}" uploaded successfully! I've analyzed ${response.data.pageCount} pages. Ask me anything about this document.`,
        timestamp: new Date()
      }]);

      showNotification(`Uploaded and analyzed successfully!`, 'success');
    } catch (error) {
      showNotification('Upload failed: ' + (error.response?.data?.error || error.message), 'error');
    } finally {
      setUploading(false);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || !chatId) return;

    const userMessage = inputMessage.trim();
    setInputMessage('');
    setSending(true);

    setMessages(prev => [...prev, { role: 'user', content: userMessage, timestamp: new Date() }]);

    try {
      const response = await axios.post(`${API_BASE_URL}/chat`, { chatId, message: userMessage });
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: response.data.response,
        timestamp: new Date(),
        responseTime: response.data.responseTime
      }]);
    } catch (error) {
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date(),
        isError: true
      }]);
    } finally {
      setSending(false);
    }
  };

  const handleDownloadExcel = async () => {
    if (!documentId) {
      showNotification('Please upload a document first', 'error');
      return;
    }

    try {
      const response = await axios.get(`${API_BASE_URL}/download/${documentId}`, {
        responseType: 'blob',
        timeout: 30000
      });

      const blob = new Blob([response.data], {
        type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
      });

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;

      let filename = `${documentInfo?.filename.replace('.pdf', '')}_extracted_${Date.now()}.xlsx`;
      const disposition = response.headers['content-disposition'];
      if (disposition) {
        const match = disposition.match(/filename="?([^"]+)"?/);
        if (match) filename = match[1];
      }

      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      showNotification('Excel downloaded!', 'success');
    } catch (error) {
      // Fallback legacy
      try {
        const legacy = await axios.get(`${API_BASE_URL}/upload/download/${documentId}`, { responseType: 'blob' });
        const blob = new Blob([legacy.data], { type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' });
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', `${documentInfo?.filename.replace('.pdf', '')}_extracted_${Date.now()}.xlsx`);
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        window.URL.revokeObjectURL(url);
        showNotification('Excel downloaded!', 'success');
      } catch (e) {
        showNotification('Download failed', 'error');
      }
    }
  };

  // ==================== GOOGLE DRIVE UPLOAD WITH OAUTH ====================
  const handleUploadToDrive = async () => {
    if (!documentId) {
      showNotification('Please upload a document first', 'error');
      return;
    }

    setUploadingToDrive(true);
    setDriveLink(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/download/drive/${documentId}`);

      if (response.data.success) {
        setDriveLink(response.data.driveLink);
        showNotification('Uploaded to Google Drive!', 'success');
      }
    } catch (error) {
      const data = error.response?.data;

      if (data?.authUrl) {
        // Open Google OAuth in popup
        const popup = window.open(
          `http://localhost:5000${data.authUrl}`,
          'google-drive-login',
          'width=500,height=600,scrollbars=yes'
        );

        if (!popup) {
          showNotification('Please allow popups for Google login', 'error');
          setUploadingToDrive(false);
          return;
        }

        showNotification('Complete Google login to upload...', 'info');

        // Poll for popup close
        const checkClosed = setInterval(async () => {
          if (popup.closed) {
            clearInterval(checkClosed);
            showNotification('Retrying upload...', 'info');
            setTimeout(() => handleUploadToDrive(), 1000); // retry
          }
        }, 1000);

        // Optional: listen for message from popup (advanced)
        window.addEventListener('message', function handler(e) {
          if (e.origin !== 'http://localhost:5000') return;
          if (e.data === 'drive-auth-success') {
            window.removeEventListener('message', handler);
            clearInterval(checkClosed);
            setTimeout(() => handleUploadToDrive(), 1000);
          }
        });
      } else {
        showNotification(
          'Upload failed: ' + (data?.error || error.message),
          'error'
        );
      }
    } finally {
      setUploadingToDrive(false);
    }
  };
  // ==================== END GOOGLE DRIVE ====================

  return (
    <div className="App">
      {/* Notification */}
      {notification && (
        <div className={`notification-toast ${notification.type}`}>
          <div className="notification-content">
            <span className="notification-icon">
              {notification.type === 'success' ? 'Check' : notification.type === 'error' ? 'Warning' : 'Info'}
            </span>
            <span className="notification-message">{notification.message}</span>
          </div>
        </div>
      )}

      <header className="app-header">
        <h1>Financial Document Chatbot</h1>
        <p>Upload your financial documents and chat with AI</p>
      </header>

      <div className="main-container">
        {/* Upload Section */}
        <div className="upload-section">
          <div className="upload-card">
            <h2>Upload Document</h2>
            <div className="file-input-wrapper">
              <input
                type="file"
                accept=".pdf"
                onChange={handleFileChange}
                disabled={uploading}
                id="file-input"
              />
              <label htmlFor="file-input" className={`file-input-label ${file ? 'has-file' : ''}`}>
                <span className="file-upload-icon">
                  {file ? 'Check' : 'Folder'}
                </span>
                <div className="file-upload-text">
                  {file ? file.name : 'Select PDF Document'}
                </div>
                <div className="file-upload-hint">
                  {file ? 'File ready to upload' : 'Click here to browse files'}
                </div>
              </label>
            </div>

            <button
              onClick={handleUpload}
              disabled={!file || uploading}
              className="btn btn-primary"
            >
              {uploading ? (
                <>
                  <span className="spinner"></span>
                  Uploading...
                </>
              ) : (
                'Upload & Analyze'
              )}
            </button>

            {documentInfo && (
              <div className="document-info">
                <h3>Document Info</h3>
                <div className="info-grid">
                  <div className="info-item">
                    <span className="info-label">Name:</span>
                    <span className="info-value">{documentInfo.filename}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Pages:</span>
                    <span className="info-value">{documentInfo.pageCount}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Processing Time:</span>
                    <span className="info-value">{(documentInfo.processingTime / 1000).toFixed(2)}s</span>
                  </div>
                </div>
              </div>
            )}

            {documentId && (
              <div className="download-section">
                <div className="download-banner">
                  <div className="download-icon">Chart</div>
                  <div className="download-text">
                    <h4>Excel Report Ready</h4>
                    <p>Your document has been processed and converted</p>
                  </div>
                </div>

                <button onClick={handleDownloadExcel} className="btn btn-download">
                  <span className="download-arrow">Down Arrow</span>
                  Download Excel File
                </button>

                <button
                  onClick={handleUploadToDrive}
                  disabled={uploadingToDrive}
                  className="btn btn-drive"
                >
                  {uploadingToDrive ? (
                    <>
                      <span className="spinner"></span>
                      Uploading to Drive...
                    </>
                  ) : (
                    <>
                      <span className="drive-icon">Cloud</span>
                      Upload to Google Drive
                    </>
                  )}
                </button>

                {driveLink && (
                  <div className="drive-success">
                    <div className="drive-success-content">
                      <span className="drive-success-icon">Check</span>
                      <span className="drive-success-text">Uploaded to Google Drive!</span>
                    </div>
                    <a
                      href={driveLink}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="btn btn-view-drive"
                    >
                      Link Open in Google Drive
                    </a>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Chat Section */}
        <div className="chat-section">
          <div className="chat-card">
            <h2>Chat with AI</h2>

            <div className="messages-container">
              {messages.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-icon">Document</div>
                  <p>Upload a document to start chatting</p>
                </div>
              ) : (
                messages.map((msg, index) => (
                  <div key={index} className={`message ${msg.role}`}>
                    <div className="message-header">
                      <span className="message-role">
                        {msg.role === 'user' ? 'You' : 'AI Assistant'}
                      </span>
                      {msg.responseTime && (
                        <span className="response-time">
                          Time {(msg.responseTime / 1000).toFixed(2)}s
                        </span>
                      )}
                    </div>
                    <div className="message-content">{msg.content}</div>
                    <div className="message-timestamp">
                      {msg.timestamp.toLocaleTimeString()}
                    </div>
                  </div>
                ))
              )}
              <div ref={messagesEndRef} />
            </div>

            <form onSubmit={handleSendMessage} className="chat-input-form">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="Ask about the document..."
                disabled={sending || !chatId}
                className="chat-input"
              />
              <button
                type="submit"
                disabled={sending || !chatId || !inputMessage.trim()}
                className="btn btn-send"
              >
                {sending ? 'Hourglass' : 'Send'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
